import { useState } from 'react';
import { Col, Row, Input, Button, Select, Tag } from 'antd';
import Todo from '../Todo';
import { useDispatch, useSelector } from 'react-redux'
import { addTodo } from '../../redux/actions';
import { v4 as uuidv4} from  'uuid';
import { todoListSelector, searchTextSelector, todoRemainingSelector } from '../../redux/selectors';
import filtersSlice from '../Filters/FiltersSlice'
import todoListSlice from './TodosSlice'
export default function TodoList() {
  const [ todoName, setTodoName ] = useState('')
  const [ prioriry, setPriority ] = useState('Medium')
  
  const todoList = useSelector(todoRemainingSelector)
  const dispatch = useDispatch()

  const handleAddButtonClick = () => {
    if(todoName){
      dispatch(
        todoListSlice.actions.addTodo({
          id: uuidv4(),
          name: todoName,
          prioriry: prioriry,
          completed: false
      }))
      setTodoName('')
      setPriority('Medium')
    }
  }
  const handleInputChange = (e) => {
    setTodoName(e.target.value)
  }
  const handleSelectChange = (value) => {
    setPriority(value)
  }

  return (
    <Row style={{ height: 'calc(100% - 40px)' }}>
      <Col span={24} style={{ height: 'calc(100% - 40px)', overflowY: 'auto' }}>
        {todoList.map( (todo) => (<Todo name={todo.name} prioriry={todo.prioriry} key={todo.id} completed = {todo.completed}/>))}
      </Col>
      <Col span={24}>
        <Input.Group style={{ display: 'flex' }} compact>
          <Input value={todoName} onChange={ handleInputChange}/>
          <Select defaultValue="Medium" onChange={handleSelectChange} value = {prioriry}>
            <Select.Option value='High' label='High'>
              <Tag color='red'>High</Tag>
            </Select.Option>
            <Select.Option value='Medium' label='Medium'>
              <Tag color='blue'>Medium</Tag>
            </Select.Option>
            <Select.Option value='Low' label='Low'>
              <Tag color='gray'>Low</Tag>
            </Select.Option>
          </Select>
          <Button type='primary' onClick = {handleAddButtonClick}>
            Add
          </Button>
        </Input.Group>
      </Col>
    </Row>
  );
}
