const initialState = {
    filters: {
        search: '',
        status: 'All',
        prioriry: []
    },
}


const filtersReducer = (state = initialState, action) => {
    switch(action.type) {
        case 'filters/searchFilterChange':
            return{
                ...state.filters,
                search: action.payload
            }
        default:
            return state
    }

}

export default filtersReducer
