import { Col, Row, Input, Typography, Radio, Select, Tag } from 'antd';
import { useState } from 'react';
import { useDispatch } from 'react-redux';
// import { searchFilterChange, statusFilterChange } from '../../redux/actions'
import FiltersSlice from './FiltersSlice';
import { todoRemainingSelector } from '../../redux/selectors';
const { Search } = Input;

export default function Filters() {
  const dispatch = useDispatch()
  const [ search, setSearch ] = useState("")
  const [ status, setStatus ] =  useState('All')
  const [ prioriry, setPriority ] = useState([])
  
  const handleSearchChange = (e) => {
    setSearch(e.target.value)
    dispatch(FiltersSlice.actions.searchFilterChange(e.target.value))
  }
  const handleStatusChange = (e) => {
    setStatus(e.target.value)
    dispatch(FiltersSlice.actions.statusFilterChange(e.target.value))
  }
  const handlePrioritiesChange = (value) =>{
    setPriority(value)
    dispatch(FiltersSlice.actions.prioritiesFilterChange(value))
  }
  return (
    <Row justify='center'>
      <Col span={24}>
        <Typography.Paragraph
          style={{ fontWeight: 'bold', marginBottom: 3, marginTop: 10 }}
        >
          Search
        </Typography.Paragraph>
        <Search placeholder='input search text' value={search} onChange={handleSearchChange}  />
      </Col>
      <Col sm={24}>
        <Typography.Paragraph
          style={{ fontWeight: 'bold', marginBottom: 3, marginTop: 10 }}
        >
          Filter By Status
        </Typography.Paragraph>
        <Radio.Group value={status} onChange={handleStatusChange}>
          <Radio value='All'>All</Radio>
          <Radio value='Completed'>Completed</Radio>
          <Radio value='Todo'>To do</Radio>
        </Radio.Group>
      </Col>
      <Col sm={24}>
        <Typography.Paragraph
          style={{ fontWeight: 'bold', marginBottom: 3, marginTop: 10 }}
        >
          Filter By Priority
        </Typography.Paragraph>
        <Select
          mode='multiple'
          allowClear
          placeholder='Please select'
          style={{ width: '100%' }}
          value={prioriry}
          onChange={handlePrioritiesChange}
        >
          <Select.Option value='High' label='High'>
            <Tag color='red'>High</Tag>
          </Select.Option>
          <Select.Option value='Medium' label='Medium'>
            <Tag color='blue'>Medium</Tag>
          </Select.Option>
          <Select.Option value='Low' label='Low'>
            <Tag color='gray'>Low</Tag>
          </Select.Option>
        </Select>
      </Col>
    </Row>
  );
}
